import './Game.css';
import Card from './Card.js'
import React, { useState } from 'react';

function Game() {

    const [shapeArr, setShapeArr] = useState(["rectangle","triangle","diamond"]);
    const [colorArr, setColorArr] = useState(["yello","green","red"]);
    const [numArr, setNumArr] = useState([1,2,3]);
    const [shadingArr, setShadingArr] = useState(["fill","transparent","pattern-checkers"]);

    const [deck, setDeck] = useState([]);
    const [deckIndex, setDeckIndex] = useState(0);
    const [currentDeck, setCurrentDeck] = useState([]);

    const generate_deck = () => {
        let card_deck = []
        for(let shape in shapeArr){
            for(let color in colorArr){
                for(let number in numArr){
                    for(let shading in shadingArr){
                        let card = {};
                        card.shape = shape;
                        card.color = color;
                        card.number = number;
                        card.shading = shading;
                        card_deck.push(card);
                    }
                }
            }
        }
        setDeck(card_deck);
    }

    const start_game = () => {
        console.log("start game")
        setDeckIndex(0);
        shuffle_card();
        create_new_current_deck();
        console.log("cards deck:" + JSON.stringify(currentDeck))
    }

    const create_new_current_deck = () => {
        setCurrentDeck([]);
        for(let i = 0; i < 3; i++){
            draw_cards();
        }
    }

    const draw_cards = () => {
        console.log("draw cards")
        let endIndex = deckIndex + 3;
        setCurrentDeck(currentDeck => [...currentDeck, deck[deckIndex]]);
        setDeckIndex(deckIndex+1);
        console.log(deckIndex)

        // while(deckIndex < endIndex && deckIndex < deck.length){
        //     console.log(deckIndex);
        //     setCurrentDeck(currentDeck => [...currentDeck, deck[deckIndex]]);
        //     setDeckIndex(deckIndex+1);
        // }
    }

    const shuffle_card = () => {
        for (let i = deck.length - 1; i > 0; i--) {  
            let j = Math.floor(Math.random() * (i + 1)); 
            let temp = deck[i]; 
            deck[i] = deck[j]; 
            deck[j] = temp; 
        } 
    }

    return (
        <div>
            <div className="contoller_zone">
                <div className="controller-button" onClick = {() => generate_deck()}>
                    GEN
                </div>
                <div className="controller-button" onClick = {() => start_game()}>
                    RESET
                </div>
                <div className="controller-button" onClick = {() => draw_cards()}>
                    DRAW
                </div>
            </div>
            <div className="card-zone">
                <Card shape="triangle" color="yellow" number="3" shading="pattern-checkers" />
                <Card shape="rectangle" color="red" number="2" shading="pattern-checkers"/>
                <Card shape="diamond" color="green" number="1" shading="pattern-checkers" />
                <Card/>
                <Card/>
                <Card/>
            </div>
        </div>
    );
}

export default Game;
