import React, { useState } from 'react';
import './Card.css'

function Card(props) {

    const [shape, setShape] = useState(props.shape);
    const [color, setColor] = useState(props.color);
    const [number, setNumber] = useState(props.number);
    const [shading, setShading] = useState(props.shading);

    const card_clicked = (e) => {
        console.log(e);
    }

    const get_fill = () => {
        let result = "";
        if(shading === "fill"){
            result = color;
        }else if(shading === "pattern-checkers"){
            result = "url(#" + color + ")";
        }else{
            result = "transparent"
        }
        return result;
    }

    const generate_card = () => {
        var elements = [];
        for(let i =0; i < number; i++){
            if(shape === "rectangle"){
                elements.push(
                    <div key={i}>
                        <svg className="svg-container">
                            <polygon points="10,10 40,10 40,90 10,90" className="rectangle" stroke={color} fill={get_fill()}/>
                        </svg>
                    </div>
                );
            }else if(shape === "triangle"){
                elements.push(
                    <div key={i}>
                        <svg className="svg-container">
                            <polygon points="25,10 40,90 10,90" className="triangle" stroke={color} fill={get_fill()}/>
                        </svg>
                    </div>
                );
            }else{
                elements.push(
                    <div key={i}>
                        <svg className="svg-container">
                            <polygon points="25,10 40,50 25,90 10,50" className="diamond" stroke={color} fill={get_fill()}/>
                        </svg>
                    </div>
                );
            }
        }
        return elements;
    }

    return (
        <div className="card-container" onClick={card_clicked()}>
            <svg width="0" height="0">
                <defs>
                    <pattern id={color} x="0" y="0" width="10" height="10" patternUnits="userSpaceOnUse">
                        <rect fill={color} x="0" width="5" height="5" y="0"></rect>
                        <rect fill={color}  x="5" width="5" height="5" y="5"></rect>
                    </pattern>
                </defs>
            </svg>
            {generate_card()}
        </div>
    );
}

export default Card;
