import React, {createContext, useReducer} from 'react';

const initialState = {
    shape:["rectangle","triangle","diamond"],
    color:["yellow","green","red"],
    number:[1,2,3],
    shading:["fill","transparent","pattern-checkers"],
    deck:[],
    currentDeck:[],
    currentIndex:0
};
const store = createContext(initialState);
const { Provider } = store;

const StateProvider = ( { children } ) => {
    const [state, dispatch] = useReducer((state, action) => {
        switch(action.type) {
        case 'GenerateDeck':
            console.log(JSON.stringify(state.shape));
            let card_deck = []
            for(let shape in state.shape){
                for(let color in state.color){
                    for(let number in state.number){
                        for(let shading in state.shading){
                            let card = {};
                            card.shape = state.shape[shape];
                            card.color = state.color[color];
                            card.number = state.number[number];
                            card.shading = state.shading[shading];
                            card_deck.push(card);
                        }
                    }
                }
            }
            return {
                ...state, deck:card_deck
            };

        case 'Reset':
            // shuffle_card
            let new_deck = deck;
            for (let i = new_deck.length - 1; i > 0; i--) {  
                let j = Math.floor(Math.random() * (i + 1)); 
                let temp = new_deck[i]; 
                new_deck[i] = new_deck[j]; 
                new_deck[j] = temp; 
            }

            // create_new_current_deck();
            let add_deck = [];
            let deckIndex = new_deck.length;
            for(let i = 0; i < 12; i++){
                add_deck.push(new_deck[i]);
            }

            currentDeck.filter((card, index)=>{
                return card.shape === "rectangle";
            })

            setDeck(new_deck);
            return {
                ...state, ...add_deck
            };
        default:
            throw new Error();
        };
    }, initialState);

  return <Provider value={{ state, dispatch }}>{children}</Provider>;
};

export { store, StateProvider }
