import React, { useState } from 'react';
import './Card.css'

function Card(props) {

    const [shape, setShape] = useState(props.shape);
    const [color, setColor] = useState(props.color);
    const [number, setNumber] = useState(props.number);
    const [shading, setShading] = useState(props.shading);

    const [shapeArr, setShapeArr] = useState(["rectangle","triangle","diamond"]);
    const [colorArr, setColorArr] = useState(["yello","green","red"]);
    const [numArr, setNumArr] = useState([1,2,3]);
    const [shadingArr, setShadingArr] = useState(["fill","transparent","red"]);

    const generate_card = () => {
        var elements = [];
        for(let i =0; i < 3; i++){
            if(shape === "rectangle"){
                elements.push(
                    <div>
                        <svg width="50" height="100">
                            <polygon points="10,10 40,10 40,90 10,90" className="rectangle" fill="url(#pattern-checkers)"/>
                        </svg>
                    </div>
                );
            }else if(shape === "triangle"){
                elements.push(
                    <div>
                        <svg width="50" height="100">
                            <polygon points="25,10 40,90 10,90" className="triangle" fill="url(#pattern-checkers)"/>
                        </svg>
                    </div>
                );
            }else{
                elements.push(
                    <div>
                        <svg width="50" height="100">
                            <polygon points="25,10 40,50 25,90 10,50" className="diamond" fill="url(#pattern-checkers)"/>
                        </svg>
                    </div>
                );
            }
            elements.push(<div>{i}</div>);
        }
        return elements;
    }

    return (
        <div className="card-container">
            <svg width="0" height="0">
                <defs>
                    <pattern id="pattern-checkers" x="0" y="0" width="10" height="10" patternUnits="userSpaceOnUse">
                        <rect class="checker" x="0" width="5" height="5" y="0"></rect>
                        <rect class="checker" x="5" width="5" height="5" y="5"></rect>
                    </pattern>
                </defs>
            </svg>
            {generate_card()}
        </div>
    );
}

export default Card;
