import React, { useState, useContext } from 'react';
import './Card.css'
import { store } from './Store.js';

function Card(props) {

    const [number, setNumber] = useState(props.number);
    const [shading, setShading] = useState(props.shading);

    const globalState = useContext(store);
    const { state, dispatch } = globalState;

    const get_fill = () => {
        let result = "";
        if(shading === "fill"){
            result = color;
        }else if(shading === "pattern-checkers"){
            result = "url(#" + color + ")";
        }else{
            result = "transparent"
        }
        return result;
    }

    const generate_card = () => {
        var elements = [];
        for(let i =0; i < props.number; i++){
            if(props.shape === "rectangle"){
                elements.push(
                    <div key={i}>
                        <svg className="svg-container">
                            <polygon points="10,10 40,10 40,90 10,90" className="rectangle" stroke={props.color} fill={get_fill()}/>
                        </svg>
                    </div>
                );
            }else if(props.shape === "triangle"){
                elements.push(
                    <div key={i}>
                        <svg className="svg-container">
                            <polygon points="25,10 40,90 10,90" className="triangle" stroke={props.color} fill={get_fill()}/>
                        </svg>
                    </div>
                );
            }else{
                elements.push(
                    <div key={i}>
                        <svg className="svg-container">
                            <polygon points="25,10 40,50 25,90 10,50" className="diamond" stroke={props.color} fill={get_fill()}/>
                        </svg>
                    </div>
                );
            }
        }
        return elements;
    }

    const card_on_click = (e) => {
        dispatch({type:"CARDCLICK", data_key:e.target.getAttribute("data_key")});
    }

    const getClassName = () => {
        if(state.clickedCard.includes(props.data_key.toString())){
            return ["card-container", "card-clicked"].join(" ");
        }else{
            return "card-container";
        }
    }

    return (
        <div data_key={props.data_key} 
            className={getClassName()} 
            onClick={(e)=>card_on_click(e)}>
            <svg width="0" height="0">
                <defs>
                    <pattern id={color} x="0" y="0" width="10" height="10" patternUnits="userSpaceOnUse">
                        <rect fill={props.color} x="0" width="5" height="5" y="0"></rect>
                        <rect fill={props.color}  x="5" width="5" height="5" y="5"></rect>
                    </pattern>
                </defs>
            </svg>
            {generate_card()}
        </div>
    );
}

export default Card;
