import './Game.css';
import Card from './Card.js'
import React, { useState, useContext, useEffect } from 'react';
import { store } from './Store.js';

function Game() {

    const [shapeArr, setShapeArr] = useState(["rectangle","triangle","diamond"]);
    const [colorArr, setColorArr] = useState(["yellow","green","red"]);
    const [numArr, setNumArr] = useState([1,2,3]);
    const [shadingArr, setShadingArr] = useState(["fill","transparent","pattern-checkers"]);

    const [deck, setDeck] = useState([]);
    const [currentDeck, setCurrentDeck] = useState([]);

    const globalState = useContext(store);
    const { dispatch } = globalState;

    useEffect(() => {
        generate_deck();
    }, []);

    const generate_deck = () => {
        let card_deck = []
        for(let shape in shapeArr){
            for(let color in colorArr){
                for(let number in numArr){
                    for(let shading in shadingArr){
                        let card = {};
                        card.shape = shapeArr[shape];
                        card.color = colorArr[color];
                        card.number = numArr[number];
                        card.shading = shadingArr[shading];
                        card_deck.push(card);
                    }
                }
            }
        }
        setDeck(card_deck);
        console.log(globalState);
        dispatch({ type: 'action description' })
    }

    const clear = () => {
        setCurrentDeck([]);
    }

    const start_game = () => {
        // shuffle_card
        let new_deck = deck;
        for (let i = new_deck.length - 1; i > 0; i--) {  
            let j = Math.floor(Math.random() * (i + 1)); 
            let temp = new_deck[i]; 
            new_deck[i] = new_deck[j]; 
            new_deck[j] = temp; 
        }

        // create_new_current_deck();
        let add_deck = [];
        let deckIndex = new_deck.length;
        for(let i = 0; i < 12; i++){
            add_deck.push(new_deck[i]);
        }

        currentDeck.filter((card, index)=>{
            return card.shape === "rectangle";
        })

        setDeck(new_deck);
        setCurrentDeck(currentDeck => [...add_deck]);
    }

    const create_new_current_deck = () => {
        setCurrentDeck([]);
        let add_deck = [];
        let deckIndex = currentDeck.length;
        for(let i = 0; i < 12; i++){
            add_deck.push(deck[i]);
        }
        setCurrentDeck(currentDeck => [...currentDeck, ...add_deck]);
    }

    const draw_cards = () => {
        if(currentDeck.length < deck.length){
            let add_deck = [];
            let deckIndex = currentDeck.length;
            for(let i = deckIndex; i < deckIndex+3; i++){
                add_deck.push(deck[i]);
            }
            setCurrentDeck(currentDeck => [...currentDeck, ...add_deck]);
            console.log("cards deck:" + JSON.stringify(currentDeck))
        }else{
            alert("No More Cards");
        }
    }

    const shuffle_card = () => {
        let new_deck = deck;
        for (let i = new_deck.length - 1; i > 0; i--) {  
            let j = Math.floor(Math.random() * (i + 1)); 
            let temp = new_deck[i]; 
            new_deck[i] = new_deck[j]; 
            new_deck[j] = temp; 
        }
        setDeck(new_deck);
    }



    const generate_cards = () => {
        var elements = [];
        let number = currentDeck.length;
        for(let i = 0; i < number; i++){
            let card = currentDeck[i];
            elements.push(
                <Card key={i} data_key={i} shape={card.shape} color={card.color} number={card.number} shading={card.shading}/>
            );
        }
        return elements;
    }

    return (
        <div>
            <div className="contoller_zone">
                <div className="controller-button" onClick = {() => clear()}>
                    CLEAR
                </div>
                <div className="controller-button" onClick = {() => start_game()}>
                    RESET
                </div>
                <div className="controller-button" onClick = {() => draw_cards()}>
                    DRAW
                </div>
                <div className="controller-button">
                    {deck.length - currentDeck.length}
                </div>
            </div>
            <div className="card-zone">
                {generate_cards()}
            </div>
        </div>
    );
}

export default Game;
