import './Game.css';
import Card from './Card.js'
import React, { useState, useContext, useEffect } from 'react';
import { store } from './Store.js';

function Game() {

    const [shapeArr, setShapeArr] = useState(["rectangle","triangle","diamond"]);
    const [colorArr, setColorArr] = useState(["yello","green","red"]);
    const [numArr, setNumArr] = useState([1,2,3]);
    const [shadingArr, setShadingArr] = useState(["fill","transparent","pattern-checkers"]);

    const [deck, setDeck] = useState([]);
    const [currentDeck, setCurrentDeck] = useState([]);

    // const globalState = useContext(store);
    // console.log(globalState);

    useEffect(() => {
        generate_deck();
    }, []);

    const generate_deck = () => {
        let card_deck = []
        for(let shape in shapeArr){
            for(let color in colorArr){
                for(let number in numArr){
                    for(let shading in shadingArr){
                        let card = {};
                        card.shape = shape;
                        card.color = color;
                        card.number = number;
                        card.shading = shading;
                        card_deck.push(card);
                    }
                }
            }
        }
        setDeck(card_deck);
    }


    const start_game = () => {
        console.log("start game")
        console.log("cards deck:" + JSON.stringify(currentDeck))
        shuffle_card();
        create_new_current_deck();
    }

    const create_new_current_deck = () => {
        setCurrentDeck([]);
        for(let i = 0; i < 4; i++){
            draw_cards();
        }
    }

    const draw_cards = () => {
        if(currentDeck.length <= deck.length){
            console.log("draw cards");
            let add_deck = [];
            let deckIndex = currentDeck.length + 3
            for(let i = 0; i < 3; i++){
                add_deck.push(deck[deckIndex]);
            }
            setCurrentDeck(currentDeck => [...currentDeck, add_deck]);
            console.log("cards deck:" + JSON.stringify(currentDeck))
        }else{
            alert("No More Cards");
        }
    }

    const shuffle_card = () => {
        for (let i = deck.length - 1; i > 0; i--) {  
            let j = Math.floor(Math.random() * (i + 1)); 
            let temp = deck[i]; 
            deck[i] = deck[j]; 
            deck[j] = temp; 
        } 
    }

    const generate_cards = () => {
        var elements = [];
        let number = currentDeck.length;
        for(let i =0; i < number; i++){
            if(shape === "rectangle"){
                elements.push(
                    <div>
                        <svg className="svg-container">
                            <polygon points="10,10 40,10 40,90 10,90" className="rectangle" stroke={color} fill={get_fill()}/>
                        </svg>
                    </div>
                );
            }else if(shape === "triangle"){
                elements.push(
                    <div>
                        <svg className="svg-container">
                            <polygon points="25,10 40,90 10,90" className="triangle" stroke={color} fill={get_fill()}/>
                        </svg>
                    </div>
                );
            }else{
                elements.push(
                    <div>
                        <svg className="svg-container">
                            <polygon points="25,10 40,50 25,90 10,50" className="diamond" stroke={color} fill={get_fill()}/>
                        </svg>
                    </div>
                );
            }
        }
        return elements;
    }

    return (
        <div>
            <div className="contoller_zone">
                <div className="controller-button" onClick = {() => generate_deck()}>
                    GEN
                </div>
                <div className="controller-button" onClick = {() => start_game()}>
                    RESET
                </div>
                <div className="controller-button" onClick = {() => draw_cards()}>
                    DRAW
                </div>
            </div>
            <div className="card-zone">
                <Card shape="triangle" color="yellow" number="3" shading="pattern-checkers" />
                <Card shape="rectangle" color="red" number="2" shading="pattern-checkers"/>
                <Card shape="diamond" color="green" number="1" shading="pattern-checkers" />
                <Card/>
                <Card/>
                <Card/>
            </div>
        </div>
    );
}

export default Game;
